        ListTag<CompoundTag> sto_ench = book.getDataTag().getListTag("StoredEnchantments");
        for (Enchantment ench : enchantments) {
            if (ench == null) {
                continue;
            }
            CompoundTag ench_tag = ENCH_TAG.copy();
            ench_tag.put("lvl", ench.getLevel());
