    /** Returns an empty instance of this {@link io.wolfscript.database.DataAccess} object

throws:
SQLException /
    private JdbcConnectionManager(SQLType type) throws SQLException {
        DatabaseConfiguration cfg = Configuration.getDbConfig();
        cpds = new ComboPooledDataSource();
        this.type = type;
        if (type.usesJDBCManager()) {
            try {
                cpds.setDriverClass(type.getClassPath());
                cpds.setJdbcUrl(cfg.getDatabaseUrl(type.getIdentifier()));
                cpds.setUser(cfg.getDatabaseUser());
                cpds.setPassword(cfg.getDatabasePassword());

                // For settings explanations see
                // http://javatech.org/2007/11/c3p0-connectionpool-configuration-rules-of-thumb/
                // https://community.jboss.org/wiki/HowToConfigureTheC3P0ConnectionPool?_sscc=t

                //connection pooling
                cpds.setAcquireIncrement(cfg.getAcquireIncrement());
                cpds.setMaxIdleTime(cfg.getMaxConnectionIdleTime());
                cpds.setMaxIdleTimeExcessConnections(cfg.getMaxExcessConnectionsIdleTime());
                cpds.setMaxPoolSize(cfg.getMaxPoolSize());
                cpds.setMinPoolSize(cfg.getMinPoolSize());
                cpds.setNumHelperThreads(cfg.getNumHelperThreads());
                cpds.setUnreturnedConnectionTimeout(cfg.getReturnConnectionTimeout());
                cpds.setIdleConnectionTestPeriod(cfg.getConnectionTestFrequency());

                //Statement pooling
                cpds.setMaxStatements(cfg.getMaxCachedStatements());
                cpds.setMaxStatementsPerConnection(cfg.getMaxCachedStatementsPerConnection());
                cpds.setStatementCacheNumDeferredCloseThreads(cfg.getNumStatementCloseThreads());
            }
            catch (PropertyVetoException e) {
                log.error("Failed to configure the connection pool!", e);
            }
            //Test connection...
            //If this fails it throws an SQLException so we're notified
            Connection c = cpds.getConnection();
            c.close();
        }
        else {
            nonManaged = DriverManager.getConnection(cfg.getDatabaseUrl(type.getIdentifier()), cfg.getDatabaseUser(), cfg.getDatabasePassword());
            nonManaged.close();
        }
    }

    /** Get the SQL Database type.

DatabaseAccessException /
    private static JdbcConnectionManager getInstance() throws DatabaseAccessException {
        if (instance == null) {
            try {
                SQLType type = SQLType.forName(Configuration.getServerConfig().getDatasourceType());
                if (type == null) {
                    throw new DatabaseAccessException(Configuration.getServerConfig().getDatasourceType() + " is not a valid JDBC Database type or has not been registered for use.");
                }
                instance = new JdbcConnectionManager(type);
            }
            catch (SQLException e) {
                throw new DatabaseAccessException("Unable to instantiate Connection Pool!", e);
            }
        }
        return instance;
    }

    /** Get a connection form the connection pool.

return:
the type /
    public SQLType getType() {
        return this.type;
    }

    /** Create a new instance of this connection manager.

connection from the pool /
    public static Connection getConnection() {
        try {
            JdbcConnectionManager cman = getInstance();
            if (!cman.type.usesJDBCManager()) {
                if (cman.nonManaged != null) {
                    if (!cman.nonManaged.isClosed()) {
                        return cman.nonManaged;
                    }
                }
                DatabaseConfiguration cfg = Configuration.getDbConfig();
                cman.nonManaged = DriverManager.getConnection(cfg.getDatabaseUrl(cman.type.getIdentifier()), cfg.getDatabaseUser(), cfg.getDatabasePassword());
                return cman.nonManaged;
            }
            return cman.cpds.getConnection();
        }
        catch (SQLException e) {
            log.error("Couldn't get a Connection from pool!", e);
            return null;
        }
        catch (DatabaseAccessException e) {
            log.error("Couldn't get a Connection from pool!", e);
            return null;
        }
    }

    /** Shut down the connection pool. Should be called when the system is reloaded or goes down to prevent data loss.

author:
Chris (damagefilter) /
public abstract class Database {

    /** The datasource type

throws:
DatabaseWriteException when an SQLException was raised or when the data type doesn't match the objects type /
    private void setToStatement(int index, Object o, PreparedStatement ps, Column.DataType t) throws DatabaseWriteException {
        try {
            switch (t) {
                case BYTE:
                case INTEGER:
                case LONG:
                case SHORT:
                case BOOLEAN: //SQlite doesn't know boolean values, it converts it to tinyint
                    ps.setInt(index, (Integer)o);
                    break;
                case FLOAT:
                case DOUBLE:
                    ps.setDouble(index, (Double)o);
                    break;
                case STRING:
                    ps.setString(index, (String)o);
            }
        }
        catch (SQLException e) {
            throw new DatabaseWriteException("Failed to set property to prepared statement!", e);
        }
        catch (ClassCastException e) {
            throw new DatabaseWriteException("Failed to set property to prepared statement!", e);
        }
    }

    public List<String> getColumnNames(DataAccess data) {
        Statement s = null;
        ResultSet rs = null;

        ArrayList<String> columns = new ArrayList<String>();
        String columnName;

        try {
            s = JdbcConnectionManager.getConnection().createStatement();
            rs = s.executeQuery("SELECT FROM '" + data.getName() + "'");
            ResultSetMetaData rsMeta = rs.getMetaData();
            for (int index = 1; index <= rsMeta.getColumnCount(); index++) {
                columnName = rsMeta.getColumnLabel(index);
                columns.add(columnName);
            }
        }
        catch (SQLException ex) {
            log.error(ex.getMessage(), ex);
        }
        finally {
            close(null, s, rs);
        }
        return columns;
    }

    public String getDataTypeSyntax(Column.DataType type) {
        switch (type) {
            case BYTE:
            case INTEGER:
            case LONG:
            case SHORT:
            case BOOLEAN:
                return "INTEGER";
            case FLOAT:
            case DOUBLE:
                return "REAL";
            case STRING:
                return "TEXT";
        }
        return "";
    }

    /** Gets a Java List representation from the SQLite String.

retur:
n /
    private List<Comparable<?>> getList(Column.DataType type, String field) {
        List<Comparable<?>> list = new ArrayList<Comparable<?>>();
        if (field == null) {
            return list;
        }
        switch (type) {
            case BYTE:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Byte.valueOf(s));
                }
                break;
            case INTEGER:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Integer.valueOf(s));
                }
                break;
            case FLOAT:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Float.valueOf(s));
                }
                break;
            case DOUBLE:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Double.valueOf(s));
                }
                break;
            case LONG:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Long.valueOf(s));
                }
                break;
            case SHORT:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Short.valueOf(s));
                }
                break;
            case STRING:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(s);
                }
                break;
            case BOOLEAN:
                for (String s : field.split(this.LIST_REGEX)) {
                    if (s.equals(NULL_STRING)) {
                        list.add(null);
                        continue;
                    }
                    list.add(Boolean.valueOf(s));
                }
                break;
        }
        return list;
    }

    private String generateQuery(DataAccess data) throws DatabaseTableInconsistencyException {
        StringBuilder fields = new StringBuilder();
        StringBuilder values = new StringBuilder();
        HashMap<Column, Object> columns = data.toDatabaseEntryList();
        Iterator<Column> it = columns.keySet().iterator();

        Column column;
        while (it.hasNext()) {
            column = it.next();
            if (!column.autoIncrement()) {
                fields.append("`").append(column.columnName()).append("`").append(",");
                values.append("?").append(",");
            }
        }
        if (fields.length() > 0) {
            fields.deleteCharAt(fields.length() - 1);
        }
        if (values.length() > 0) {
            values.deleteCharAt(values.length() - 1);
        }
        return "INSERT INTO `" + data.getName() + "` (" + fields.toString() + ") VALUES(" + values.toString() + ")";
    }

